#include<bits/stdc++.h>using namespace std;int main() {    ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);    int N, M; cin >> N >> M;    bool chk[N] = {false, };    while(M-->0) {        int A; char B; cin >> A >> B;        if(B=='M') {            if(!chk[A-1]) cout << "Yes\n";            else cout << "No\n";            chk[A-1]=true;        } else {            cout << "No\n";        }    }}