#include<bits/stdc++.h>using namespace std;int main() {    ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);    int t; cin >> t;    while(t-->0) {        int n; cin >> n;        string s[n];        for(int i=0;i<n;i++) cin >> s[i];        for(int i=n-1;i>=0;i--) {            for(int j=0;j<4;j++) {                if(s[i][j]=='#') {                    cout << j+1 << ' ';                    break;                }            }        }        cout << '\n';    }}